    private void jBPotenciaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBPotenciaActionPerformed
       String cadena;
       int n;
       cadena = jText1.getText(); //obtiene la cadena
       n = Integer.parseInt(txtpotencia.getText()); //obtiene el valor de la potencia
       obj.Potencia(cadena,n);// envia los valores de la cadena y la potencia al metodo potencia
       jLabelResult.setText(obj.Potencia(cadena,n));// envia el resultado
    }//GEN-LAST:event_jBPotenciaActionPerformed

