    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       String cadena;
       cadena = jText1.getText(); //obtiene la cadena
       obj.Inversa(cadena,cadena.length()-1); //envia los valores de la cadena y la longitud -1 de esta al metodo 
       jLabelResult.setText(obj.Inversa(cadena,cadena.length()-1));// muestra el resultado
    }//GEN-LAST:event_jButton1ActionPerformed

